<script>

export default {
  data(){
    return{
      pageTitle : "",
      isActive : false
    }
  },
  props: {
    toggleSidebar: {
      type: Function,
      required: true
    },
    openSidebar:{
      type : Boolean,
      required : true
    }
  },
  methods: {
    goBack() {
      if (window.history.length > 1) {
        this.$router.back(); // Quay lại trang trước
      } else {
        this.$router.push("/"); // Nếu không có trang trước, quay về trang chủ
      }
    },
    changeActive(){
      this.isActive = !this.isActive
    }
  },
  watch: {
    $route(to) {
      this.pageTitle = to.meta.title;
    },
  },
  created() {
    this.pageTitle = this.$route.meta.title;
  },
}
</script>